mapper.select:
select

  distinct


from entity




  order by ${orderByClause}



    limit ${offset}, ${rows}


    limit ${rows}

select count(e.id) from entity e
left join order_put op on e.pid = op.id left join orders ord on op.oid = ord.id
left join put p on e.pid = p.id left join plan pl on p.pid = pl.id
left join put p on e.pid = p.id
left join put p on e.pid = p.id left join plan pl on p.pid = pl.id
where 1=1
and p.put_type in
#{putType}
and ord.cust_id = #{custId}
and pl.adver_id = #{custId}
and pl.adver_id = #{custId}
and ord.cust_id in
#{adver}
and pl.adver_id in
#{adver}
and pl.adver_id
#{adver}

select e.id id, e.entity_name entityName, e.entity_state entityState, e.run_state runState
,op.put_name putName,ord.name orderName,cust.full_name adverName
,p.put_name putName,pl.plan_name planName,cust.full_name adverName
left join put p on e.pid = p.id left join plan pl on p.pid = pl.id
from entity e
left join order_put op on e.pid = op.id left join orders ord on op.oid = ord.id left join company cust on ord.cust_id = cust.id
left join put p on e.pid = p.id left join plan pl on p.pid = pl.id left join company cust on pl.adver_id = cust.id
left join put p on e.pid = p.id
left join put p on e.pid = p.id left join plan pl on p.pid = pl.id
where 1=1
and p.put_type in
#{putType}
and ord.cust_id = #{custId}
and pl.adver_id = #{custId}
and pl.adver_id = #{custId}
and ord.cust_id in
#{adver}
and pl.adver_id in
#{adver}
and pl.adver_id
#{adver}
and e.entity_state in
#{entityState}
and e.entity_state != 0
order by e.run_state desc,e.id desc
limit ${start},${limit}
