mapper.select:
select

	distinct


from put




	order by ${orderByClause}


	limit ${start},${limit}

select count(p.id)
from put p
 left join plan pl on p.pid = pl.id

where 1=1




			and pl.adver_id = #{custId}





				and pl.adver_id in


				#{cust}






		and p.put_state in

			#{putState}



		and p.put_state != 0



	and p.create_user in

		#{uid}

select
ap.`name` positionName,type.dic_value typeName, template.dic_value templateName,
GROUP_CONCAT(CONCAT_WS('-',p.begin_time,p.end_time)) times from put p left join ad_position ap on p.ad_position = ap.id
left join dictionary type on ap.type = type.id left join dictionary template on ap.template_id = template.id left join orders ord on p.oid = ord.id
where p.put_state != 0
and ord.cust_id = #{custId}
and ord.id = #{oid}
and p.flow_source = #{flowId}
and ap.template_id = #{templateId}
and ap.type = #{typeId}
and p.ad_position = #{positionId}
and #{dateStr} between DATE_FORMAT(p.begin_time,'%Y%m') and DATE_FORMAT(p.end_time,'%Y%m')
group by p.ad_position order by p.ad_position
